import React, { Component } from "react";

class HomePage extends Component {
    state = {
        myImg: ""
    }

    handleSubmit = (e) => {
        if (e.target.snap.value && e.target.snap.value !== "") {
            addSnap(e.target.snap.value).then((res) => {
                this.setState({myImg: res.data});
            });
        }
        e.target.snap.value = "";
        e.preventDefault();
    }

    UNSAFE_componentWillMount() {
        snaplist().then((value) => {
            this.setState({myImg: value.data});
        });
    }

    render() {
        const { myImg } = this.state;
        let coord = [];
        for (let x = 0; x < (15 * 70); x += 70)
            coord.push(x);
        console.log(coord);
        return(
            <div style={{border: "2px solid black"}}>
                <h2>Hello</h2>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" name="snap" placeholder="Your snap"/>
                    <input type="submit" value="Envoyer"/>
                </form>
                {myImg !== "" &&
                    <div style={{ display: "flex", flexWrap: "wrap"}}>
                        {coord.map((value, index) => (
                            <div key={index} style={{display: "block", width: "70px", height: "70px", overflow: "hidden"}}>
                                <img src={myImg} alt="snaps" style={{marginLeft: "-" + value + "px"}}/>
                            </div>
                        ))}
                    </div>
                }
            </div>
        )
    }
}

const snaplist = () => {
    return fetch("http://localhost:8080/snaplist").then((response) => {
        return response.json();
    });
}

const addSnap = (snap) => {
    if (snap && snap !== "") {
        return fetch("http://localhost:8080/push/"+snap).then((response) => {
            return response.json();
        });
    }
}
export default HomePage;
