import React, { Component } from "react";

class HomePage extends Component {
    state = {
        myImg: "",
        users: [],
        isAdmin: false
    }

    handleSubmit = (e) => {
        if (e.target.snap.value && e.target.snap.value !== "") {
            addSnap(e.target.snap.value).then((res) => {
                let users = res.data.files.map((value) => {
                    return value.replace(/\.jpg/, "");
                });
                this.setState({myImg: res.data.pic, users});
            });
        }
        e.target.snap.value = "";
        e.preventDefault();
    }

    handleDelete = (username) => {
        delSnap(username).then((res) => {
            let users = res.data.files.map((value) => {
                return value.replace(/\.jpg/, "");
            });
            this.setState({myImg: res.data.pic, users});
        });
    }

    UNSAFE_componentWillMount() {
        snaplist().then((res) => {
            let users = res.data.files.map((value) => {
                return value.replace(/\.jpg/, "");
            });
            this.setState({myImg: res.data.pic, users});
        });
    }

    render() {
        const { myImg, users, isAdmin } = this.state;
        let coord = [];
        for (let x = 0, i = 0; x < (users.length * 100); x += 100, i++)
            coord.push({x, user: users[i]});
        return(
            <div style={{border: "2px solid black", display: "flex", flex: 1, flexDirection: "column", alignItems: "center"}}>
                <h2 style={{color: "white"}}>LOL</h2>
                <form onSubmit={this.handleSubmit} style={{margin: "15px"}}>
                    <input type="text" name="snap" placeholder="Your snap"/>
                    <input type="submit" value="Envoyer"/>
                </form>
                {myImg !== "" &&
                    <div style={{ display: "flex", flexWrap: "wrap", width: "400px" }}>
                        {coord.map((value, index) => (
                            <div key={index} style={{display: "block", width: "100px", height: "100px", overflow: "hidden"}}>
                                <img src={myImg} width={100 * 16} alt="snaps" style={{marginLeft: "-" + value.x + "px"}}/>
                                {(isAdmin) && 
                                    <span onClick={() => { this.handleDelete(value.user) }} style={{backgroundColor: "rgba(255, 255, 255, 0.8)", position: "relative", top: "-100px", left: "70px", fontSize: "25px", color: "#B53471", fontWeight: "bold", cursor: "pointer"}}>&#10005;</span>
                                }
                                <div style={{width: "100px", height: "100px", backgroundColor: "rgba(255, 255, 255, 0.5)", position: "relative", top: "-105px", zIndex: "10"}} onMouseEnter={(e) => { e.target.style.display = "none" }} onMouseLeave={(e) => { e.target.style.display = null }}></div>
                            </div>
                        ))}
                    </div>
                }
            </div>
        )
    }
}

const snaplist = () => {
    return fetch("http://localhost:8080/snaplist").then((response) => {
        return response.json();
    });
}

const addSnap = (snap) => {
    if (snap && snap !== "") {
        return fetch("http://localhost:8080/push/"+snap).then((response) => {
            return response.json();
        });
    }
}

const delSnap = (snap) => {
    if (snap && snap !== "") {
        return fetch("http://localhost:8080/remove/"+snap, {
            method: "DELETE"
        }).then((response) => {
            return response.json();
        });
    }
}
export default HomePage;
