import React, { Component } from "react";

class HomePage extends Component {
    state = {
        myImg: ""
    }

    handleSubmit = (e) => {
        addSnap(e.target.snap.value).then((res) => {
            console.log(res);
        });
        e.preventDefault();
    }

    UNSAFE_componentWillMount() {
        snaplist().then((value) => {
            this.setState({myImg: value.data});
        });
    }

    render() {
        const { myImg } = this.state;
        return(
            <div style={{border: "2px solid black"}}>
                <h2>Hello</h2>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" name="snap" placeholder="Your snap"/>
                    <input type="submit" value="Envoyer"/>
                </form>
                {myImg !== "" &&
                    <div style={{ display: "flex"}}>
                        <div style={{display: "block", width: "320px", height: "320px", overflow: "hidden", margin: "auto"}}>
                            <img src={myImg} alt="snaps"/>
                        </div>
                        <div style={{display: "block", width: "320px", height: "320px", overflow: "hidden", margin: "auto"}}>
                            <img src={myImg} alt="snaps" style={{marginLeft: "-320px"}}/>
                        </div>
                        <div style={{display: "block", width: "320px", height: "320px", overflow: "hidden", margin: "auto"}}>
                            <img src={myImg} alt="snaps" style={{marginLeft: "-640px"}}/>
                        </div>
                        <div style={{display: "block", width: "320px", height: "320px", overflow: "hidden", margin: "auto"}}>
                            <img src={myImg} alt="snaps" style={{marginLeft: "-960px"}}/>
                        </div>
                        <div style={{display: "block", width: "320px", height: "320px", overflow: "hidden", margin: "auto"}}>
                            <img src={myImg} alt="snaps" style={{marginLeft: "-1280px"}}/>
                        </div>
                    </div>
                }
            </div>
        )
    }
}

const snaplist = () => {
    return fetch("http://localhost:8080/snaplist").then((response) => {
        return response.json();
    });
}

const addSnap = (snap) => {
    if (snap && snap !== "") {
        return fetch("http://localhost:8080/push/"+snap).then((response) => {
            return response.json();
        });
    }
}
export default HomePage;
