import React, { Component } from "react";

class HomePage extends Component {
    state = {
        myImg: "",
        users: []
    }

    handleSubmit = (e) => {
        if (e.target.snap.value && e.target.snap.value !== "") {
            addSnap(e.target.snap.value).then((res) => {
                let users = res.data.files.map((value) => {
                    return value.replace(/\.jpg/, "");
                });
                this.setState({myImg: res.data.pic, users});
            });
        }
        e.target.snap.value = "";
        e.preventDefault();
    }

    handleDelete = (username) => {
        delSnap(username).then((res) => {
            let users = res.data.files.map((value) => {
                return value.replace(/\.jpg/, "");
            });
            this.setState({myImg: res.data.pic, users});
        });
    }

    UNSAFE_componentWillMount() {
        snaplist().then((res) => {
            let users = res.data.files.map((value) => {
                return value.replace(/\.jpg/, "");
            });
            this.setState({myImg: res.data.pic, users});
        });
    }

    render() {
        const { myImg, users } = this.state;
        let coord = [];
        for (let x = 0, i = 0; x < (users.length * 70); x += 70, i++)
            coord.push({x, user: users[i]});
        return(
            <div style={{border: "2px solid black"}}>
                <h2 style={{color: "white"}}>The Deep Snap</h2>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" name="snap" placeholder="Your snap"/>
                    <input type="submit" value="Envoyer"/>
                </form>
                {myImg !== "" &&
                    <div style={{ display: "flex", flexWrap: "wrap"}}>
                        {coord.map((value, index) => (
                            <div key={index} style={{display: "block", width: "70px", height: "170px", overflow: "hidden"}}>
                                <img src={myImg} alt="snaps" style={{marginLeft: "-" + value.x + "px"}}/>
                                <button style={{backgroundColor: "red"}} onClick={() => { this.handleDelete(value.user) }}>Del</button>
                            </div>
                        ))}
                    </div>
                }
            </div>
        )
    }
}

const snaplist = () => {
    return fetch("http://localhost:8080/snaplist").then((response) => {
        return response.json();
    });
}

const addSnap = (snap) => {
    if (snap && snap !== "") {
        return fetch("http://localhost:8080/push/"+snap).then((response) => {
            return response.json();
        });
    }
}

const delSnap = (snap) => {
    if (snap && snap !== "") {
        return fetch("http://localhost:8080/remove/"+snap, {
            method: "DELETE"
        }).then((response) => {
            return response.json();
        });
    }
}
export default HomePage;
